import React from "react";
import ReactDOM from "react-dom";
import { createStore } from "redux";
import counter from "./reducer";
import { incrementCounter, decrementCounter,evenOddCounter } from "./action";



const store = createStore(counter);

console.log(store.getState()); 
let inputVal='';

const handleInput = (e) => {
  inputVal=e.target.value
}

function App() {
  return (
    <div className="App">
      Count : {store.getState().count}

      <br />
      <input onChange={(e) =>handleInput(e)}></input>
      <br></br>
      <button onClick={() => store.dispatch(incrementCounter(parseInt(inputVal)))}>
        Increment
      </button>
      <br></br>
      <br />
      <button onClick={() => store.dispatch(decrementCounter(parseInt(inputVal)))}>
        Decrement
      </button><br></br>
      <br></br>

      <button onClick={() => store.dispatch(evenOddCounter(parseInt(inputVal)))}>
        oddEvenincrement
      </button><br></br>
      <br></br>


    </div>
  );
}

const rootElement = document.getElementById("root");
const render = () => ReactDOM.render(<App />, rootElement);
render();
store.subscribe(render);
export default App;
