import React from "react";
import ReactDOM from "react-dom";
import { createStore } from "redux";
import counter from "./reducer";
import { incrementCounter, decrementCounter } from "./action";

// We createa a store. We pass counter as an argument
//
const store = createStore(counter);
console.log(incrementCounter(1));
// store.dispatch(incrementCounter(1))
console.log(store.getState()); // Log the initial state
// Store functions
// getState
// subscribe
// dispatch

function App() {
  return (
    <div className="App">
      Count : {store.getState().count}
      <br />
      <button onClick={() => store.dispatch(incrementCounter(1))}>
        Increment by 1
      </button>
      <br />
      <button onClick={() => store.dispatch(decrementCounter(1))}>
        Decrement by 1
      </button>
    </div>
  );
}

const rootElement = document.getElementById("root");
const render = () => ReactDOM.render(<App />, rootElement);
render();
store.subscribe(render);
