import React from "react";
import ReactDOM from "react-dom";
import { createStore } from "redux";
import counter from "./reducer";
import { incrementCounter, decrementCounter } from "./action";



const store = createStore(counter);
console.log(incrementCounter(1));
console.log(store.getState()); 


function App() {
  return (
    <div className="App">
      Count : {store.getState().count}

      <br />
      <br></br>
      <button onClick={() => store.dispatch(incrementCounter(document.getElementById("input")))}>
        Increment
      </button>
      <br></br>
      <br />
      <button onClick={() => store.dispatch(decrementCounter(document.getElementById("input")))}>
        Decrement
      </button><br></br>
      <br></br>
      <button onClick={() => store.dispatch( oddincrementCounter(document.getElementById("input")))}>
        Odd Increment
      </button><br></br>
      <br></br>
      <button onClick={() => store.dispatch( evenincrementCounter(document.getElementById("input")))}>
        Even increment
      </button><br></br>
      <br></br>

      <input type="text" id="input"> </input>
    </div>
  );
}

const rootElement = document.getElementById("root");
const render = () => ReactDOM.render(<App />, rootElement);
render();
store.subscribe(render);
export default App;
