import React from 'react';
import './App.css';
import { Route, BrowserRouter, Link } from "react-router-dom";
import Navbar from './Components/Navbar';
import Breakfast from './Components/Breakfast'

import Lunch from './Components/Lunch';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      Breakfast: [{

        name: "  Pancakes and Maple Syrup",
        address: "60, Jyothi Nivas College Road, Koramangala 5th Block, Bang",
        rating: 4.6,
        Cost: "₹300",
      },
      {

        name: "  Eggs Benedict ",
        address: "635, 100 Feet Road, Koramangala 6th Block, Bangalore",
        rating: 4.2,
        Cost: "₹400",

      },
      {

        name: "Bagel with Cream Cheese",
        address: "183/16, 5th Main Road, 4th Block, Jayanagar, Bangalore",
        rating: 4.4,
        Cost: "₹400",
      },
      {

        name: "Cinnamon Rolls ",
        address: "118, Koramangala Industrial Area, Koramangala 7th Blo",
        rating: 4.6,
        Cost: "₹150",
      },
      {
        name: "Belgian Style Waffles ",
        address: "718, 1st C Main, 45th Cross, 8th Block, Jayanagar, Bangalore",
        rating: 4.6,
        Cost: "₹200",
      },
      {

        name: "Breakfast Burrito ",
        address: "1209, 100 Feet Road, Opposite Apollo Clinic, Indiranag",
        rating: 4.6,
        Cost: "₹190",


      },
    ],


    Lunch: [{

      name: "Allahabad Ki Tehri",
      address: "60, Jyothi Nivas College Road, Koramangala 5th Block, Bang",
      rating: 4.2,
      Cost: "₹289",
    },
    {
      name:" Low Fat Dahi Chicken",
      rating:4.3,
      Cost:499
    },
    {
      name:"Kolhapuri Vegetables",
      rating:4.5,
      Cost:299
    },
    {
      name:"Urlai Roast",
      rating:4.3,
      Cost:370

    },
    {
      name:" Paneer Achaari",
      rating:3.3,
      Cost:400
    },
    {
      name:" Dal Makhani",
      rating:4.4,
      Cost:299
    },
    {
      name:"Black Channa and Coconut Stew",
      rating:4.3,
      Cost:270
    },
    {
      name:"Urlai Roast",
      rating:300,
      Cost:4.2
    },
    {
      name:"Biryani",
      rating:4.4,
      Cost:370
    }


  ]
}
}





render() {


  return (
    <BrowserRouter>
      <Navbar />
      <div className="App ">
        <div className="row ml-5">
          <a className="ml-5">

            <br></br>
            <Link to="/breakfast"><h5>BreakFast</h5></Link>
          </a>

          <a className="ml-5">

            <br></br>
            <Link to="/lunch"><h5>Lunch</h5></Link>
          </a>

          <a className="ml-5">

            <br></br>
            <Link to="/dinner"><h5>Dinner</h5></Link>
          </a>

        </div>

        <Route path="/breakfast" exact render={() => <Breakfast input={this.state.Breakfast} />} />
        <Route path="/lunch" render={() => <Lunch input={this.state.Lunch} />} />

      </div>
    </BrowserRouter>
  );
}
}


export default App;
