    {
      name:" Dal Makhani",
      rating:4.4,
      Cost:299
    },
    {
      name:"Black Channa and Coconut Stew",
      rating:4.3,
      Cost:270
    },
    {
      name:"Urlai Roast",
      rating:300,
      Cost:4.2
    },
    {
      name:"Biryani",
      rating:4.4,
      Cost:370
    }


  ],
  Dinner :[{
    name:"BUTTER CHICKEN",
    rating:4.6,
    Cost:280
  },
  {
    name:"TANDOORI CHICKEN",
    rating:4.3,
    Cost:450
  },
  {
    name:"MALAI KOFTA",
    rating:4.7,
    Cost:180
