  {
    name:"MATAR PANEER",
    rating:4.1,
    Cost:280
  },
  {
    name:"Chana Masala",
    rating:3.2,
    Cost:120
  },
  {
    name:"Chili Chicken",
    rating:4.0,
    Cost:180
  },
  {
    name:"Navratan Korma (Nine Gem Curry)",
    rating:3.9,
    Cost:180
  },
  {
    name:"Uthapam",
    rating:3.9,
    Cost:220
  },
  {
    name:"Sambar",
    rating:3.8,
    Cost:120
  },
  {
    name:"SAMOSAS",
    rating:4.2,
    Cost:120
  }

  ]
}
}





render() {


  return (
    <BrowserRouter>
      <Navbar />
      <div className="App ">
        <div className="row ml-5">
          <a className="ml-5">

            <br></br>
            <Link to="/breakfast"><h5>BreakFast</h5></Link>
          </a>

          <a className="ml-5">

            <br></br>
            <Link to="/lunch"><h5>Lunch</h5></Link>
          </a>

          <a className="ml-5">

            <br></br>
            <Link to="/dinner"><h5>Dinner</h5></Link>
          </a>

        </div>

        <Route path="/breakfast" exact render={() => <Breakfast input={this.state.Breakfast} />} />
        <Route path="/lunch" render={() => <Lunch input={this.state.Lunch} />} />
        <Route path="/dinner" render={() => <Dinner input={this.state.Dinner} />} />
      </div>
    </BrowserRouter>
  );
}
}


export default App;
